import './App.css';
import React, {useEffect,useState} from 'react'
import { BrowserRouter, Routes, Route, useNavigate } from "react-router-dom";

function GamePage({thumbnail, info, links, descList}) {
  const [gameList, setGameList] = useState([]);
  const navigate = useNavigate();

  useEffect(()=> {
    let routeList = [];
    for (let i = 0; i < links.length; i++) {
      routeList.push(<button key = {i} onClick={()=>navigate(links[i])}>{descList[i]["name"]}</button>);
    }
    setGameList(routeList);
  },[]);

  return (
    <div>
      <h1>{info["name"]}</h1>
      <img src={thumbnail} alt="thumbnail"/>
      {gameList}
    </div>
  );
}

function Home() {
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };
    handleScroll();

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    }
  }, [])
  return (
    <div className="App-header">
      <div className="header">
        <img id="logo-picture" src="/pngs/zeronialogo-2021.png" alt="logo"/>
        <span className="snowstorm" id="logo-text">Zeronia</span>
        <div className="tabs">
          <span>Home</span>
          <span>Announcements</span>
          <span>Map Gallery</span>
          <span>About</span>
        </div>
      </div>
    </div>
  );
}

function App() {
  const [gameRoutes, setGameRoutes] = useState([]);
  const [homeRoute, setHomeRoute] = useState([]);

  useEffect(()=> {
    fetch("/api").then(response => response.json()).then(data => {
      let descList = data["descs"];
      let routeList = [];
      let linkPaths = [];

      for (let i = 0; i < descList.length; i++) {
        let str = "/" + descList[i]["name"].replace(" ", "_")
        routeList.push(<Route key = {i} path={str} element={<GamePage info={descList[i]} 
          thumbnail={data["thumbnails"][i]} links={linkPaths} descList={descList}/>} />);
        linkPaths.push(str);
      }

      setGameRoutes(routeList);
      setHomeRoute(<Route path="/" element={<Home/>} />);
    });
  },[]);

  return (
    <BrowserRouter>
      <Routes>
        {homeRoute}
        {gameRoutes}
      </Routes>
    </BrowserRouter>
  );
}

export default App;
